const items = $input.all();
  const oneDayAgo = new Date();
  oneDayAgo.setDate(oneDayAgo.getDate() - 1);

  return items.filter(item => {
    const pubDate = new Date(item.json.isoDate);
    return pubDate >= oneDayAgo;});

const items = $input.all();

  return items.map(item => {
    const text = item.json.output;
    const maxLength = 2000;
    const children = [];

    for (let i = 0; i < text.length; i += maxLength) {
      children.push({
        object: "block",
        type: "paragraph",
        paragraph: {
          rich_text: [{
            type: "text",
            text: {content: text.substring(i, i + maxLength)}
          }]
        }
      });
    }

    // ✅ 이전 노드에서 title과 link 가져오기
    const editFields = $node['Edit Fields'].json;

    return {
      json: {
        parent: {
          database_id: "2909c221-6eb8-80ea-85de-edaaad3ef6c0"
        },
        properties: {
          name: {
            title: [{
              text: {
                content: editFields.title || 'Untitled'  // ✅ 수정
              }
            }]
          }
        },
        children: children
      }
    };
  });
